var Stream = require('stream').Stream
var util = require('util')

function ReadableStream(length, chunksize) {
    Stream.call(this)
    var self = this
    this.readable = true

    this.init = function() {

        for (var i=0;i<length;i+=chunksize) {
            var buf = new Buffer(chunksize)
            buf.fill('x')
            self.emit('data', buf)
        }

        self.emit('end')
    }

}

util.inherits(ReadableStream, Stream)

module.exports = ReadableStream
